#!/usr/bin/env python# -*- encoding: utf-8 -*-# @Author  :   {Titanxz}# @Time    :   2019/8/23 17:36# @License :   (C) Copyright 2019, {python_1904}# 服务器 提供下载 一张图片# 导包from socket import *# 初始化信息ip = '192.168.1.17'port = 6636buf_size = 1024address = (ip, port)# 创建套接字tcp_server_socket = socket(AF_INET, SOCK_STREAM)# 绑定 监听tcp_server_socket.bind(address)tcp_server_socket.listen(5)# 数据传递file = open('p(9).jpg', 'rb')while 1:    print('等待客户端的连接...')    client_socket, client_address = tcp_server_socket.accept()    print('来自%s的连接...' % str(client_address))    image_data = file.read()    if not image_data:        continue    client_socket.send(image_data)    print('数据传输完毕...')    # 关闭资源    client_socket.close()tcp_server_socket.close()